import React from 'react'
import {Â setIsPasswordVisible } from '../../features/ui/'
const Input = ({ name, type, value, placeholder, id, icon, helperText }) => {
  if (!icon) {
    icon = `fi-br-question`
  }

  return (
    <div className='relative w-[100%] mb-4'>
      {helperText && (
        <p className='font-bold text-sm mb-2 ml-1'>{helperText}</p>
      )}
      <div className='relative'>
        <input
          id={id}
          name={name}
          type={type}
          placeholder={placeholder}
          defaultValue={value}
          className='input-box pl-10' 
        />
        <div className='absolute left-3 top-1/2 transform -translate-y-1/2 mt-0.5 ml-1'>
          <i className={`${`fi`} ${icon}`}></i>
        </div>

        {
          type === 'password' && (
            <i className='fi fi-rr-eye-crossed input-icon left-[auto] right-4 cursor-pointer'></i>
          )
        }
      </div>
    </div>
  )
}

export default Input
