import React from 'react'

const Input = ({ name, type, value, placeholder, id, icon, helperText }) => {

  if (!icon) {
    icon = `fi-br-question` // If the icon prop is not set correctly, then we should see a question mark icon.
  }

  return (
    <div className='relative w-[100%] mb-4'>
      {
        helperText && (
          <p className='font-gelasio text-sm'>{helperText}</p>
        )
      }
      <input 
        id={id}
        name={name}
        type={type}
        placeholder={placeholder}
        defaultValue={value}
        className='input-box'
      />
      <i className={`${`fi`} ${icon} ${`input-icon`}`}></i>
    </div>
  )
}

export default Input
